// src/components/CharacterList.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllCharacters } from "../../services/rickAndMortyApi";
import "./CharacterList.css";
import CharacterFilter from "../CharactersFilter/CharacterFilter";
import CharacterSearch from "../CharacterSearch/CharacterSearch";

const CharacterList = () => {
  const [characters, setCharacters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    species: "",
    gender: "",
    status: "",
    name: "", // Guardar el nombre para la búsqueda
  });

  const fetchCharacters = async () => {
    setLoading(true);
    try {
      const data = await getAllCharacters(filters);
      setCharacters(data.results || []);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const applyFilters = () => {
    fetchCharacters(); // Solo se llama cuando el botón de filtro se presiona
  };

  const handleSearch = (searchTerm) => {
    // Actualiza el estado de filters para incluir el término de búsqueda
    setFilters((prevFilters) => ({
      ...prevFilters,
      name: searchTerm, // Actualizamos el estado de name con el término de búsqueda
    }));
    fetchCharacters(); // Ejecuta la búsqueda de inmediato
  };

  useEffect(() => {
    fetchCharacters(); // Ejecuta esto una vez al cargar el componente
  }, []); // Solo carga los personajes inicialmente

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <div className="character-controls">
        <CharacterSearch onSearch={handleSearch} className="character-search" />{" "}
        {/* Búsqueda a la izquierda */}
        <CharacterFilter
          filters={filters}
          setFilters={setFilters}
          onFilter={applyFilters}
          className="character-filters"
        />
      </div>

      <div className="character-list">
        {characters.map((character) => (
          <div className="character-card" key={character.id}>
            <img src={character.image} alt={character.name} />
            <h3>{character.name}</h3>
            <Link to={`/character/${character.id}`}>
              <button className="btn-more">Ver más</button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CharacterList;
