// src/components/CharacterList.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllCharacters } from "../../services/rickAndMortyApi";
import "./CharacterList.css";
import CharacterFilter from "../CharactersFilter/CharacterFilter";

const CharacterList = () => {
  const [characters, setCharacters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    species: "",
    gender: "",
    status: "",
  });

  const fetchCharacters = async () => {
    setLoading(true);
    try {
      const data = await getAllCharacters(filters);
      setCharacters(data.results); // Actualiza la lista de personajes
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const applyFilters = () => {
    getAllCharacters(); // Se llama a fetchCharacters al aplicar filtros
  };

  const clearFilters = () => {
    // Restablece los filtros
    setFilters({
      species: "",
      gender: "",
      status: "",
    });
    fetchCharacters(); // Vuelve a cargar todos los personajes sin filtros
  };

  useEffect(() => {
    fetchCharacters(); // Solo se ejecuta una vez al cargar el componente
  }, []); // Se carga inicialmente sin filtros

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="character-list">
      <CharacterFilter
        filters={filters}
        setFilters={setFilters} // Este setFilters se utiliza para actualizar los filtros
        onFilter={applyFilters} // Llama a applyFilters para aplicar filtros
        onClear={clearFilters} // Llama a clearFilters para limpiar filtros
      />
      <div className="character-list">
        {characters.map((character) => (
          <div className="character-card" key={character.id}>
            <img src={character.image} alt={character.name} />
            <h3>{character.name}</h3>
            <Link to={`/character/${character.id}`}>
              <button className="btn-more">Ver m√°s</button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CharacterList;
