// src/components/CharacterList.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllCharacters } from "../../services/rickAndMortyApi";
import "./CharacterList.css";
import CharacterFilter from "../CharactersFilter/CharacterFilter";
const CharacterList = () => {
  const CharacterList = () => {
    const [characters, setCharacters] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [filters, setFilters] = useState({
      species: "",
      gender: "",
      status: "",
    });
    const [isFiltered, setIsFiltered] = useState(false); // Estado para rastrear si hay filtros aplicados

    const fetchCharacters = async () => {
      setLoading(true);
      try {
        const data = await getAllCharacters(filters);
        setCharacters(data.results);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    const applyFilters = () => {
      setIsFiltered(true); // Marcar como filtrado al aplicar filtros
      fetchCharacters();
    };

    const clearFilters = () => {
      setFilters({
        species: "",
        gender: "",
        status: "",
      });
      setIsFiltered(false); // Restablecer estado de filtrado
      fetchCharacters(); // Volver a cargar todos los personajes sin filtros
    };

    useEffect(() => {
      fetchCharacters();
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
      <div className="character-list">
        <CharacterFilter
          filters={filters}
          setFilters={setFilters}
          onFilter={applyFilters}
          onClear={clearFilters}
          isFiltered={isFiltered} // Pasar el estado de filtrado a los filtros
        />
        <div className="character-list">
          {characters.map((character) => (
            <div className="character-card" key={character.id}>
              <img src={character.image} alt={character.name} />
              <h3>{character.name}</h3>
              <Link to={`/character/${character.id}`}>
                <button className="btn-more">Ver m√°s</button>
              </Link>
            </div>
          ))}
        </div>
      </div>
    );
  };
};

export default CharacterList;
