import React, { useState } from "react";
import "./CharacterFilter.css";

const CharacterFilter = ({ onFilter }) => {
  // Estados para cada filtro
  const [species, setSpecies] = useState("");
  const [gender, setGender] = useState("");
  const [status, setStatus] = useState("");

  // Función que llama a `onFilter` pasando los valores seleccionados
  const handleFilterClick = () => {
    onFilter({ species, gender, status });
  };

  return (
    <div className="character-filters">
      {/* Selector para especie */}
      <select value={species} onChange={(e) => setSpecies(e.target.value)}>
        <option value="">Especie</option>
        <option value="Human">Humano</option>
        <option value="Alien">Alien</option>
        {/* Puedes agregar más especies según sea necesario */}
      </select>

      {/* Selector para género */}
      <select value={gender} onChange={(e) => setGender(e.target.value)}>
        <option value="">Género</option>
        <option value="Male">Masculino</option>
        <option value="Female">Femenino</option>
        <option value="Genderless">Sin Género</option>
        <option value="unknown">Desconocido</option>
      </select>

      {/* Selector para estado */}
      <select value={status} onChange={(e) => setStatus(e.target.value)}>
        <option value="">Estado</option>
        <option value="alive">Vivo</option>
        <option value="dead">Muerto</option>
        <option value="unknown">Desconocido</option>
      </select>

      {/* Botón para aplicar los filtros */}
      <button className="btn-filter" onClick={handleFilterClick}>
        Filtrar
      </button>
    </div>
  );
};

export default CharacterFilter;
