// src/components/CharacterList.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllCharacters } from "../../services/rickAndMortyApi";
import "./CharacterList.css";
import CharacterFilter from "../CharactersFilter/CharacterFilter";
import CharacterSearch from "../CharacterSearch/CharacterSearch";
const CharacterList = () => {
  const [characters, setCharacters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    species: "",
    gender: "",
    status: "",
    name: "", // Agrega el campo para nombre
  });
  const [favorites, setFavorites] = useState([]); // Estado para los personajes favoritos
  const [showFavorites, setShowFavorites] = useState(false);

  const fetchCharacters = async () => {
    setLoading(true);
    try {
      const data = await getAllCharacters(filters);
      setCharacters(data.results || []);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (searchTerm) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      name: searchTerm, // Actualiza solo el nombre en los filtros
    }));
    fetchCharacters(); // Busca personajes después de actualizar el filtro
  };

  const applyFilters = () => {
    fetchCharacters(); // Solo se llama cuando el botón de filtro se presiona
  };

  useEffect(() => {
    fetchCharacters(); // Llama a fetchCharacters cuando se monta el componente
  }, []); // Solo carga los personajes inicialmente

  const toggleFavorite = (character) => {
    setFavorites((prevFavorites) => {
      if (prevFavorites.some((fav) => fav.id === character.id)) {
        // Si ya es favorito, lo quitamos
        return prevFavorites.filter((fav) => fav.id !== character.id);
      } else {
        // Si no es favorito, lo añadimos
        return [...prevFavorites, character];
      }
    });
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <div className="character-controls">
        <CharacterSearch onSearch={handleSearch} className="character-search" />{" "}
        {/* Búsqueda a la izquierda */}
        <CharacterFilter
          filters={filters}
          setFilters={setFilters}
          onFilter={applyFilters}
          className="character-filters"
        />
      </div>

      <div className="character-list">
        {characters.map((character) => (
          <div className="character-card" key={character.id}>
            <img src={character.image} alt={character.name} />
            <h3>{character.name}</h3>
            <Link to={`/character/${character.id}`}>
              <button className="btn-more">Ver más</button>
            </Link>
            <button
              onClick={() => toggleFavorite(character)}
              className="btn-favorite"
            >
              {favorites.some((fav) => fav.id === character.id)
                ? "⭐ Favorito"
                : "Añadir a Favoritos"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CharacterList;
