// src/components/CharacterList.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllCharacters } from "../../services/rickAndMortyApi";
import "./CharacterList.css";
import CharacterFilter from "../CharactersFilter/CharacterFilter";

const CharacterList = () => {
  const [characters, setCharacters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchCharacters();
  }, []);

  const fetchCharacters = async (filters = {}) => {
    const query = Object.keys(filters)
      .map((key) => (filters[key] ? `${key}=${filters[key]}` : ""))
      .filter(Boolean)
      .join("&");

    const url = query
      ? `https://rickandmortyapi.com/api/character?${query}`
      : "https://rickandmortyapi.com/api/character";

    try {
      const response = await getAllCharacters(url);
      setCharacters(response.results);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleFilter = (filters) => {
    fetchCharacters(filters);
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <CharacterFilter onFilter={handleFilter} /> {/* Agregado aquí */}
      <div className="character-list">
        {characters.map((character) => (
          <div className="character-card" key={character.id}>
            <img src={character.image} alt={character.name} />
            <h3>{character.name}</h3>
            <Link to={`/character/${character.id}`}>
              <button className="btn-more">Ver más</button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CharacterList;
