// src/components/CharacterFilte// src/components/CharacterFilter.js
import React from "react";
import "./CharacterFilter.css";

const CharacterFilters = ({ filters, setFilters, onFilter, onClear }) => {
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFilters((prevFilters) => ({
      ...prevFilters,
      [name]: value,
    }));
  };

  return (
    <div className="character-filters">
      <select
        name="species"
        onChange={handleInputChange} // Solo actualiza el estado de filtros
        value={filters.species}
      >
        <option value="">Select Species</option>
        <option value="Human">Human</option>
        <option value="Alien">Alien</option>
        {/* Agrega más opciones según lo que devuelva la API */}
      </select>

      <select
        name="gender"
        onChange={handleInputChange} // Solo actualiza el estado de filtros
        value={filters.gender}
      >
        <option value="">Select Gender</option>
        <option value="Male">Male</option>
        <option value="Female">Female</option>
        <option value="Genderless">Genderless</option>
        <option value="unknown">Unknown</option>
      </select>

      <select
        name="status"
        onChange={handleInputChange} // Solo actualiza el estado de filtros
        value={filters.status}
      >
        <option value="">Select Status</option>
        <option value="Alive">Alive</option>
        <option value="Dead">Dead</option>
        <option value="unknown">Unknown</option>
      </select>

      <button onClick={onFilter} className="btn-filter">
        Filtrar
      </button>

      {/* Botón para limpiar filtros */}
      <button
        onClick={onClear}
        className="btn-clear"
        disabled={!filters.species && !filters.gender && !filters.status} // Desactiva si no hay filtros
      >
        Limpiar filtros
      </button>
    </div>
  );
};

export default CharacterFilters;
