// src/components/CharacterList.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllCharacters } from "../../services/rickAndMortyApi";
import "./CharacterList.css";
import CharacterFilter from "../CharactersFilter/CharacterFilter";
const CharacterList = () => {
  const [characters, setCharacters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    species: "",
    gender: "",
    status: "",
  });

  const [filtered, setFiltered] = useState(false); // Estado para indicar si se ha filtrado

  const applyFilters = async () => {
    setLoading(true);
    try {
      const data = await getAllCharacters(filters); // Aplicar filtros al hacer clic en el botón
      setCharacters(data.results);
      setFiltered(true); // Indica que se aplicaron filtros
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const fetchCharacters = async () => {
      setLoading(true);
      try {
        const data = await getAllCharacters(); // Cargar todos los personajes al principio
        setCharacters(data.results);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchCharacters();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="character-list">
      <CharacterFilter
        filters={filters}
        setFilters={setFilters}
        onFilter={applyFilters}
      />
      <div className="character-list">
        {characters.map((character) => (
          <div className="character-card" key={character.id}>
            <img src={character.image} alt={character.name} />
            <h3>{character.name}</h3>
            <Link to={`/character/${character.id}`}>
              <button className="btn-more">Ver más</button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CharacterList;
