// src/components/CharacterList.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllCharacters } from "../../services/rickAndMortyApi";
import "./CharacterList.css";
import CharacterFilter from "../CharactersFilter/CharacterFilter";
import CharacterSearch from "../CharacterSearch/CharacterSearch";

const CharacterList = () => {
  const [characters, setCharacters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    species: "",
    gender: "",
    status: "",
    name: "", // Agrega el campo para nombre
  });
  const [favoriteCharacter, setFavoriteCharacter] = useState(null); // Solo un favorito

  const fetchCharacters = async () => {
    setLoading(true);
    try {
      const data = await getAllCharacters(filters);
      setCharacters(data.results || []);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (searchTerm) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      name: searchTerm, // Actualiza solo el nombre en los filtros
    }));
    fetchCharacters(); // Busca personajes despu√©s de actualizar el filtro
  };

  const applyFilters = () => {
    fetchCharacters(); // Solo se llama cuando el bot√≥n de filtro se presiona
  };

  useEffect(() => {
    fetchCharacters(); // Llama a fetchCharacters cuando se monta el componente
  }, []); // Solo carga los personajes inicialmente

  const toggleFavorite = (character) => {
    if (favoriteCharacter && favoriteCharacter.id === character.id) {
      // Si ya es favorito, lo desactivamos
      setFavoriteCharacter(null);
    } else {
      // Si no es favorito, lo activamos
      setFavoriteCharacter(character);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <div className="character-controls">
        <CharacterSearch onSearch={handleSearch} className="character-search" />
        <CharacterFilter
          filters={filters}
          setFilters={setFilters}
          onFilter={applyFilters}
          className="character-filters"
        />
      </div>

      <div className="character-list">
        {characters.map((character) => (
          <div className="character-card" key={character.id}>
            <img src={character.image} alt={character.name} />
            <h3>{character.name}</h3>
            <Link to={`/character/${character.id}`}>
              <button className="btn-more">Ver m√°s</button>
            </Link>
            <button
              onClick={() => toggleFavorite(character)}
              className="btn-favorite"
            >
              {favoriteCharacter && favoriteCharacter.id === character.id
                ? "‚ù§Ô∏è Favorito"
                : "ü§ç A√±adir a Favoritos"}
            </button>
            {favoriteCharacter && favoriteCharacter.id === character.id && (
              <button
                onClick={() => setFavoriteCharacter(null)}
                className="btn-remove-favorite"
              >
                Desactivar Favorito
              </button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CharacterList;
